list.queryText:
query{
  getEquipment(id:"$line"){
    name
    events{
      equipment{name}
      id
      Time:startDateTime
      TimeEnd:endDateTime
      duration
      reasonCode
      reasonText
      comments
      }
  }
}

query{
  getEquipment(id:"$line"){
    name
    jobResponses{
      Time:startDateTime
      TimeEnd:endDateTime
      jobOrder{
        quantity
        operationsSegment{
          operationsDefinition{
            material{
              code
              description
            }
          }
        }
      }
   }
  }
}

targets.queryText:
query{
  getEquipment(id:"$line"){
    id
    name
    jobResponses(filter:{endDateTime:{le:"${__to:date:iso}"},startDateTime:{ge:"${__from:date:iso}"}}){
      equipment { name }
      jobOrder{
        name
        scheduledStartDateTime
        scheduledEndDateTime 
        scheduledDuration
        quantity 
        operationsSegment{
          duration
          operationsDefinition{
            material {
              code
              description
            }
          }
        }
      }
      startDateTime
      endDateTime
      actualDuration 
      downtime:eventsAggregate{
        count
        durationSum
      }
      scrap:quantitiesAggregate(filter:{type:{eq:Scrap}}){
        count
        quantitySum
      }
      rework:quantitiesAggregate(filter:{type:{eq:Rework}}){
        count
        quantitySum
      }
      produced:quantitiesAggregate(filter:{type:{eq:Produced}}){
        count
        quantitySum
      }
    }
  }
}

targets.query:
// v.bucket, v.timeRangeStart, and v.timeRange stop are all variables supported by the flux plugin and influxdb
from(bucket: v.bucket)
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_value"] >= 10 and r["_value"] <= 20)

